package gfm.util;

// TODO: Auto-generated Javadoc
/**
 * The Interface Controllable.
 */
public interface Controllable {
	
	/**
	 * Up.
	 */
	void up();
	
	/**
	 * Down.
	 */
	void down();
	
	/**
	 * Left.
	 */
	void left();
	
	/**
	 * Right.
	 */
	void right();

	/**
	 * Release up.
	 */
	void releaseUp();
	
	/**
	 * Release down.
	 */
	void releaseDown();
	
	/**
	 * Release left.
	 */
	void releaseLeft();
	
	/**
	 * Release right.
	 */
	void releaseRight();
	
	/**
	 * First action.
	 */
	void firstAction();
	
	/**
	 * Second action.
	 */
	void secondAction();
	
	/**
	 * Third action.
	 */
	void thirdAction();

	/**
	 * Release first action.
	 */
	void releaseFirstAction();
	
	/**
	 * Release second action.
	 */
	void releaseSecondAction();
	
	/**
	 * Release third action.
	 */
	void releaseThirdAction();
}
